function getRandomColor(){for(var t="0123456789ABCDEF",e="#",a=0;6>a;a++)e+=t[Math.floor(16*Math.random())];return e}function draw(){context.beginPath();for(var t=0;32>t;t++)for(var e=0;32>e;e++)context.fillStyle=getRandomColor(),context.fillRect(e*TILE_SIZE,t*TILE_SIZE,TILE_SIZE,TILE_SIZE);context.stroke(),context.closePath()}function animate(){timer++,timer==maxTimer&&(timer=0,draw()),setTimeout(function(){window.requestAnimationFrame(function(){animate()})},12)}var TILE_SIZE=10,w=320,h=320,c='<canvas id="lightticker" width="'+w+'" height="'+h+'"></canvas>';document.getElementsByTagName("body")[0].innerHTML+=c;var canvas=document.getElementById("lightticker"),context=canvas.getContext("2d"),timer=0,maxTimer=50;animate();
